$(document).ready(function(){$(".spinner-wrap").click(function(){function r(){e.toggleClass("pulse")}var e=$(this),t=e.siblings("audio")[0],n=Number(e.siblings("audio").data("bpm"));pulse=60/n*500;e.toggleClass("playing");if(e.hasClass("playing")){t.play();r();intervals=setInterval(function(){r()},pulse)}else{t.pause();t.currentTime=0;clearInterval(intervals)}})})